import { Container } from "pixi.js";
import type { GameState, Unit, PlayerColor, UnitAction, Player } from "../types.js";
import { UnitRenderer } from "./UnitRenderer.js";
import { UnitLabelOverlay } from "./UnitLabelOverlay.js";
import type { CommanderTooltip } from "../ui/CommanderTooltip.js";
import type { CommanderToast } from "../ui/CommanderToast.js";
import { UNIT_MOVE_SPEED } from "../config.js";

interface PoolEntry {
  renderer: UnitRenderer;
  visualX: number;
  visualY: number;
  targetX: number;
  targetY: number;
  displayName: string;
  currentAction: UnitAction | null;
  lastClearedAt: number | undefined;
  animClear: { elapsed: number } | null;
  baseAlpha: number;
  playerRef: Player | null;  // non-null for commanders
}

export class UnitPool {
  readonly container = new Container();
  private pool = new Map<string, PoolEntry>();
  private labelOverlay: UnitLabelOverlay | null = null;
  private tooltip: CommanderTooltip | null = null;
  private toast: CommanderToast | null = null;

  setLabelOverlay(overlay: UnitLabelOverlay) {
    this.labelOverlay = overlay;
  }

  setTooltip(tooltip: CommanderTooltip) {
    this.tooltip = tooltip;
  }

  setToast(toast: CommanderToast) {
    this.toast = toast;
  }

  syncUnits(state: GameState) {
    const activeIds = new Set<string>();

    for (const player of Object.values(state.players)) {
      // Commander — pass full player ref for tooltip
      this.syncUnit(player.commander, player.color, player.status, player, activeIds);

      // Subagent units
      for (const unit of Object.values(player.units)) {
        this.syncUnit(unit, player.color, player.status, null, activeIds);
      }
    }

    // Remove units no longer in state
    for (const [id, entry] of this.pool) {
      if (!activeIds.has(id)) {
        this.container.removeChild(entry.renderer.container);
        entry.renderer.destroy();
        this.pool.delete(id);
      }
    }

    // Prune orphaned labels
    this.labelOverlay?.prune(activeIds);
  }

  private syncUnit(unit: Unit, playerColor: PlayerColor, playerStatus: Player["status"], player: Player | null, activeIds: Set<string>) {
    activeIds.add(unit.id);

    const baseAlpha = playerStatus === "disconnected" ? 0.2 : playerStatus === "idle" ? 0.6 : 1;

    let entry = this.pool.get(unit.id);
    if (!entry) {
      // New unit — create renderer
      const renderer = new UnitRenderer(unit, playerColor);
      entry = {
        renderer,
        visualX: unit.position.x,
        visualY: unit.position.y,
        targetX: unit.position.x,
        targetY: unit.position.y,
        displayName: unit.displayName,
        currentAction: unit.currentAction,
        lastClearedAt: unit.clearedAt,
        animClear: null,
        baseAlpha,
        playerRef: player,
      };
      this.pool.set(unit.id, entry);
      this.container.addChild(renderer.container);

      // Show toast quote when a new commander spawns
      if (player !== null) {
        this.toast?.show();
      }

      // Enable hover tooltip on the PixiJS sprite for commander units
      if (player !== null) {
        const capturedEntry = entry;
        renderer.container.eventMode = "static";
        renderer.container.on("pointerenter", (e) => {
          if (capturedEntry.playerRef) this.tooltip?.show(capturedEntry.playerRef, e.clientX, e.clientY);
        });
        renderer.container.on("pointerleave", () => this.tooltip?.hide());
      }
    }

    entry.baseAlpha = baseAlpha;
    entry.playerRef = player;

    // Update target position
    const target = unit.targetPosition ?? unit.position;
    entry.targetX = target.x;
    entry.targetY = target.y;

    // Update status — pass action type so the indicator colour reflects the tool
    entry.renderer.updateStatus(unit.status, unit.currentAction?.actionType ?? undefined);
    entry.displayName = unit.displayName;
    entry.currentAction = unit.currentAction;

    // Detect a new clearedAt timestamp → trigger rebirth animation
    if (unit.clearedAt !== undefined && unit.clearedAt !== entry.lastClearedAt) {
      entry.lastClearedAt = unit.clearedAt;
      entry.animClear = { elapsed: 0 };
    }
  }

  update(dt: number) {
    const CLEAR_FADE_OUT = 300; // ms: alpha 1→0
    const CLEAR_FADE_IN  = 400; // ms: alpha 0→1
    const CLEAR_TOTAL    = CLEAR_FADE_OUT + CLEAR_FADE_IN;

    for (const [id, entry] of this.pool) {
      const dx = entry.targetX - entry.visualX;
      const dy = entry.targetY - entry.visualY;
      const dist = Math.sqrt(dx * dx + dy * dy);

      if (dist < 1) {
        // Snap to target
        entry.visualX = entry.targetX;
        entry.visualY = entry.targetY;
      } else {
        // Lerp toward target
        const step = Math.min(UNIT_MOVE_SPEED * dt, dist);
        entry.visualX += (dx / dist) * step;
        entry.visualY += (dy / dist) * step;
      }

      entry.renderer.container.x = entry.visualX;
      entry.renderer.container.y = entry.visualY;

      // Drive rebirth pulse animation (fade out then fade in)
      if (entry.animClear) {
        entry.animClear.elapsed += dt * 1000;
        const t = entry.animClear.elapsed;
        if (t < CLEAR_FADE_OUT) {
          entry.renderer.container.alpha = entry.baseAlpha * (1 - t / CLEAR_FADE_OUT);
        } else if (t < CLEAR_TOTAL) {
          entry.renderer.container.alpha = entry.baseAlpha * ((t - CLEAR_FADE_OUT) / CLEAR_FADE_IN);
        } else {
          entry.renderer.container.alpha = entry.baseAlpha;
          entry.animClear = null;
        }
      } else {
        entry.renderer.container.alpha = entry.baseAlpha;
      }

      // Update HTML label position
      if (this.labelOverlay) {
        let actionText: string | null = null;
        if (entry.currentAction) {
          const fname = entry.currentAction.target.split("/").pop() ?? entry.currentAction.target;
          actionText = `${entry.currentAction.toolName}: ${fname}`;
        }
        this.labelOverlay.setLabel(id, entry.visualX, entry.visualY, entry.displayName, actionText);

        // Wire hover tooltip for commanders (idempotent)
        if (entry.playerRef && this.tooltip) {
          const e = entry;
          const t = this.tooltip;
          this.labelOverlay.setHoverable(
            id,
            (sx, sy) => { if (e.playerRef) t.show(e.playerRef, sx, sy); },
            () => t.hide(),
          );
        }
      }
    }
  }
}

import { Application } from "pixi.js";
import type { GameState } from "./types.js";
import { DEFAULT_PACK } from "./config.js";
import { loadPack } from "./assets/AssetLoader.js";

function formatTs(ts: number): string {
  const d = new Date(ts);
  const hh = String(d.getHours()).padStart(2, "0");
  const mm = String(d.getMinutes()).padStart(2, "0");
  const ss = String(d.getSeconds()).padStart(2, "0");
  return `${hh}:${mm}:${ss}`;
}
import { StateSync } from "./state/StateSync.js";
import { ReplaySync, type EventEntry } from "./state/ReplaySync.js";
import { ReplayControls } from "./ui/ReplayControls.js";
import { CommanderTooltip } from "./ui/CommanderTooltip.js";
import { CommanderToast } from "./ui/CommanderToast.js";
import { EventLog } from "./ui/EventLog.js";
import { Legend } from "./ui/Legend.js";
import { TimelineControls } from "./ui/TimelineControls.js";
import { GameLoop } from "./core/GameLoop.js";
import { Camera } from "./core/Camera.js";
import { MapRenderer } from "./renderer/MapRenderer.js";
import { MapOverlay } from "./renderer/MapOverlay.js";
import { UnitPool } from "./renderer/UnitPool.js";
import { UnitLabelOverlay } from "./renderer/UnitLabelOverlay.js";
import { PackSelector } from "./ui/PackSelector.js";

declare global {
  interface Window {
    __REPLAY_MODE__?: boolean;
  }
}

async function loadReplayEntries(): Promise<EventEntry[]> {
  const res = await fetch("/__replay/events.jsonl");
  if (!res.ok) throw new Error(`Failed to load replay: HTTP ${res.status}`);
  const text = await res.text();
  return text
    .trim()
    .split("\n")
    .filter((line) => line.length > 0)
    .map((line) => JSON.parse(line) as EventEntry);
}

async function init() {
  // Load assets (allow URL override via ?pack=name)
  const params = new URLSearchParams(window.location.search);
  const packName = params.get("pack") || DEFAULT_PACK;
  await loadPack(packName);

  // Create PixiJS application
  const app = new Application();
  await app.init({
    resizeTo: window,
    background: 0x0a0a0f,
    antialias: true,
  });

  const gameDiv = document.getElementById("game")!;
  gameDiv.appendChild(app.canvas);

  // Force canvas behind HTML overlays
  const canvas = app.canvas as HTMLCanvasElement;
  canvas.style.position = "absolute";
  canvas.style.zIndex = "0";

  // HTML overlay container — sits above canvas
  // pointer-events:none so camera drag/zoom goes through to canvas
  const overlayContainer = document.createElement("div");
  overlayContainer.style.cssText =
    "position:absolute;top:0;left:0;width:100%;height:100%;z-index:1;pointer-events:none;overflow:hidden;user-select:text;";
  gameDiv.appendChild(overlayContainer);

  // Camera (pan + zoom)
  const camera = new Camera(canvas);
  app.stage.addChild(camera.world);

  // Forward wheel events from the HTML overlay layer to the camera so zoom
  // still works when the pointer is over selectable text elements.
  overlayContainer.addEventListener("wheel", (e) => camera.onWheel(e), { passive: false });

  // Renderers
  const mapRenderer = new MapRenderer();
  const unitPool = new UnitPool();

  camera.world.addChild(mapRenderer.container);
  camera.world.addChild(unitPool.container);

  // HTML overlays for crisp text at any zoom
  const mapOverlay = new MapOverlay();
  const unitLabelOverlay = new UnitLabelOverlay();
  overlayContainer.appendChild(mapOverlay.el);
  overlayContainer.appendChild(unitLabelOverlay.el);
  unitPool.setLabelOverlay(unitLabelOverlay);

  // Right panel: [timeline strip | event log] — positioned as a unit so they
  // stay connected when EventLog is resized.
  const rightPanel = document.createElement("div");
  rightPanel.style.cssText = [
    "position:fixed",
    "top:8px",
    "right:8px",
    "max-height:calc(100vh - 16px)",
    "display:flex",
    "flex-direction:row",
    "z-index:10",
  ].join(";");

  const timeline = new TimelineControls();
  const eventLog = new EventLog();
  rightPanel.appendChild(timeline.el);
  rightPanel.appendChild(eventLog.el);
  document.getElementById("ui-overlay")!.appendChild(rightPanel);

  const legend = new Legend();
  document.getElementById("ui-overlay")!.appendChild(legend.el);

  const commanderTooltip = new CommanderTooltip();
  document.getElementById("ui-overlay")!.appendChild(commanderTooltip.el);
  unitPool.setTooltip(commanderTooltip);

  const commanderToast = new CommanderToast();
  document.getElementById("ui-overlay")!.appendChild(commanderToast.el);
  unitPool.setToast(commanderToast);

  const packSelector = new PackSelector(packName);
  document.getElementById("ui-overlay")!.appendChild(packSelector.el);

  // Center camera on map
  camera.centerOn(500, 500);

  // Determine mode and create appropriate state source
  const isReplay = !!window.__REPLAY_MODE__;
  let stateSource: StateSync | ReplaySync;

  if (isReplay) {
    const entries = await loadReplayEntries();
    const replaySync = new ReplaySync(entries);
    stateSource = replaySync;

    // Replay controls UI
    const controls = new ReplayControls(replaySync);
    document.getElementById("ui-overlay")!.appendChild(controls.el);

    replaySync.onChange((state) => {
      mapRenderer.update(state);
      mapOverlay.update(state);
      unitPool.syncUnits(state);
      eventLog.update(state);
    });
    replaySync.start();
  } else {
    const stateSync = new StateSync();
    stateSource = stateSync;

    let scrubbing = false;

    // Push renderers to a given state (used by both live and scrub paths)
    const applyState = (state: GameState) => {
      mapRenderer.update(state);
      mapOverlay.update(state);
      unitPool.syncUnits(state);
      eventLog.update(state);
    };

    stateSync.onChange((state) => {
      if (scrubbing) return;
      applyState(state);
      timeline.notifyLiveUpdate(state.eventLog.length);
    });

    timeline.onScrub((historicalState, idx, total, ts) => {
      scrubbing = true;
      mapRenderer.resetTick();
      applyState(historicalState);
      timeline.notifyLiveUpdate(total);
      eventLog.setTime(formatTs(ts));
    });

    timeline.onLive(() => {
      scrubbing = false;
      eventLog.setTime("");
      const current = stateSync.getState();
      if (current) {
        mapRenderer.resetTick();
        applyState(current);
      }
    });

    stateSync.start();
    timeline.start();
  }

  // Game loop — interpolate units each frame, sync overlays with camera
  const gameLoop = new GameLoop((dt) => {
    unitPool.update(dt);

    // Sync HTML overlays with camera transform
    mapOverlay.syncCamera(camera.worldX, camera.worldY, camera.zoom);
    unitLabelOverlay.syncCamera(camera.worldX, camera.worldY, camera.zoom);

    // Update event log header with connection status
    const state = stateSource.getState();
    if (stateSource.connected && state) {
      const players = Object.values(state.players);
      const activePlayers = players.filter(p => p.status === "active" || p.status === "idle").length;
      const totalPlayers = players.length;
      const playerStr = `${activePlayers} active/${totalPlayers}p`;
      if (stateSource instanceof ReplaySync) {
        const cursor = stateSource.getCursor();
        const total = stateSource.getLength();
        eventLog.setStatus(`replay ${cursor + 1}/${total} · tick ${state.tick} · ${playerStr}`, "#4af");
      } else if (stateSource.isFixture()) {
        eventLog.setStatus(`fixture · tick ${state.tick} · ${playerStr}`, "#cc4");
      } else {
        eventLog.setStatus(`tick ${state.tick} · ${playerStr}`, "#4f4");
      }
    } else {
      eventLog.setStatus("disconnected", "#f44");
    }
  });
  gameLoop.start();
}

